import React, { Component } from 'react'

export default class AccountSettings extends Component {
  constructor(props) {
    super(props)
    this.state = {
      email: "",
      pass: "",
      confirm_pass: ""
    }
  }

  handleChange = (e) => {
    this.setState({ [e.target.name]: e.target.value })
  }

  onSubmit = () => {

  }


  render() {
    return (
      <div>
        <form onSubmit={this.onSubmit}>
          <input type="email" name="email" value="" placeholder="confirm email" onChange={this.handleChange} />
          <input type="text" name="pass" value="" placeholder="password" onChange={this.handleChange} />
          <input type="text" name="confirm_pass" value="" placeholder="confirm password" onChange={this.handleChange} />
          <button onClick={this.onSubmit}>Sign Up</button>
        </form>
      </div>
    )
  }
}
