import React, { Component } from 'react'
import AccountSettings from './AccountSettings'
import UserDetails from './UserDetails'

export default class Dashborad extends Component {
  constructor() {
    super();
    this.state = {
      check: false
    };

  }

  switchTab = () => {
    debugger
    this.setState({
      check: !this.state.check
    })
  }
  render() {
    return (
      <div>
        <button onClick={this.switchTab}> {!check ? "Account Info" : "User Info"} </button>
        {!check ? <AccountSettings /> :
          <UserDetails />}
      </div>
    )
  }
}
