import React, { Component } from "react";
import AccountSettings from "./AccountSettings";
import UserDetails from "./UserDetails";
import Header from "./Header";
import SideNavBar from "./SideNavBar";

export default class Dashborad extends Component {
  constructor() {
    super();
    this.state = {
      check: "main",
    };
  }

  switchToMain = () => {
    this.setState({ check: "main" });
  };

  switchToAdd = () => {
    this.setState({ check: "add" });
  };

  render() {
    const { check } = this.state;
    return (
      <div>
        <Header />
        <SideNavBar />
        <button onClick={this.switchToMain}> Main Information </button>{" "}
        <button onClick={this.switchToAdd}> Additional Information </button>
        <div class="right-sec">
          {check === "main" && <AccountSettings />}
          {check === "add" && <UserDetails />}
        </div>
      </div>
    );
  }
}
